'use strict';
const chai = require('chai');
const expect = chai.expect;
const utf8 = require('../utf8.js');

describe('UTF8 string conversion tests', function () {
   const TestString = 'Ã¤Ã¶Ã¼ðŸ˜„';
   it('converts from utf8 data in a byte array to a string and back', function () {
      let array = utf8.strToArray(TestString);
      let converted = utf8.arrayToStr(array);
      expect(converted).to.equal(TestString);
   });
});